INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(599, 1, '2018-05-09 16:27:37', '2018-05-09 07:27:37', '慈恵柏病院神経内科の作田健一先生にご指名頂き、先日柏にいって講演してきました。\r\n\r\n&nbsp;\r\n\r\n作田先生は、虎の門修業時代に知り合った神経内科の後輩で、当時は2人でよくふざけ合っていたものです。\r\n\r\n&nbsp;\r\n\r\nそんな2人が座長と特別講演とは!\r\n\r\nはやいものです。\r\n\r\n&nbsp;\r\n\r\n彼は人格、熱意とも十分で、さらに脳血管内治療の専門医もお持ちです。\r\n\r\n柏の脳卒中診療のこれからを引っ張ってゆける男だと、私は信じています。\r\n\r\n&nbsp;\r\n\r\n我々だってまだまだ発展途上ですが、豊洲の4年間の活動は、いまの彼やチームにとって参考にしていただける部分も多いと思います。\r\n\r\n&nbsp;\r\n\r\n彼らにとって、地域の救急隊にとって、近隣でご開業の先生方にとって、そして柏の患者さんにとって、役に立つように。\r\n\r\n&nbsp;\r\n\r\nそんな想いを込めてお話ししました。\r\n\r\n&nbsp;\r\n\r\n多くの質問を頂き、医師や救急隊のみなさんの熱意を感じるとともに、内科と外科、救急がうまく協力して診療しているお姿も拝見できました。\r\n\r\n&nbsp;\r\n\r\n柏の脳卒中診療がますます盛り上がることをお祈りしております。\r\n\r\n&nbsp;', '脳卒中病診連携研究会 in 東葛', '', 'publish', 'closed', 'closed', '', 'blog-599', '', '', '2018-05-30 11:53:19', '2018-05-30 02:53:19', '', 0, 'http://webstyle2.com/shkt-nonai/?post_type=blog&#038;p=599', 0, 'blog', '', 0),
(600, 1, '2018-05-30 11:53:06', '2018-05-30 02:53:06', '', 'IMG_033522530', '', 'inherit', 'closed', 'closed', '', 'img_033522530', '', '', '2018-05-30 11:53:06', '2018-05-30 02:53:06', '', 599, 'http://webstyle2.com/shkt-nonai/wp_contents/wp-content/uploads/2018/05/IMG_033522530.jpg', 0, 'attachment', 'image/jpeg', 0),
(601, 1, '2018-05-14 09:34:06', '2018-05-14 00:34:06', '<p>World Live Neurovascular Conference、いってきました!</p>\r\n<p>&nbsp;</p>\r\n<p>といっても、神戸ですが。</p>\r\n<p>&nbsp;</p>\r\n<p>でも、その内容はまさに世界クラス!</p>\r\n<p>&nbsp;</p>\r\n<p>もう動脈瘤なんて、ほとんどコイルつめてません。</p>\r\n<p>&nbsp;</p>\r\n<p>頭蓋内にもばんばんフローダイバーター置いちゃって、「WEB」みたいな新しい形の塞栓デバイスを置いちゃって、おしまい。</p>\r\n<p>&nbsp;</p>\r\n<p>日本ではフローダイバーターが使える施設もまだまだすくないのに。</p>\r\n<p>&nbsp;</p>\r\n<p>従来の血管内治療ではやりにくいMCAの動脈瘤なんかも、簡便に塞栓出来ちゃって、動脈瘤治療はなんか時代がまたひとつ変わったんだなって感じました。</p>\r\n<p>&nbsp;</p>\r\n<p>治療が簡便に、安全になってゆくのは、我々にとっても、患者さんにとってもhappyなことですね!</p>\r\n<p>&nbsp;</p>\r\n<p>日本でもこんな風に使える日が早く来ることを願っています。</p>\r\n<p>&nbsp;</p>\r\n<p>そして、私の大事な目的の1つである、日本ではまだ使用できないステントリトリバーを触ってくることもできました。</p>\r\n<p>&nbsp;</p>\r\n<p>制約上、詳細は述べられないのが残念ですが、言えることは、こちらもまさに「次世代」。</p>\r\n<p>&nbsp;</p>\r\n<p>血栓や血管について考え抜いて作られている、プロのデバイスだと感じました。</p>\r\n<p>&nbsp;</p>\r\n<p>虚血の業界もまだまだ先が楽しみです。</p>\r\n<p>&nbsp;</p>\r\n<p>いま使えるデバイスをより有効に使えるよう、日々精進しつつ、新しいデバイスが使える日を待ちたいと思います。</p>', 'WLNC2018', '', 'publish', 'closed', 'closed', '', 'wlnc2018', '', '', '2020-01-10 19:00:21', '2020-01-10 10:00:21', '', 0, 'http://webstyle2.com/shkt-nonai/?post_type=blog&#038;p=601', 0, 'blog', '', 0),
